源泉的小站 https://luxcgo.github.io/posts/go-algorithms/ -<div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-java" data-lang="java"><span style="color:#228b22">// version 1: with jiuzhang template
</span><span style="color:#228b22"></span><span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#8b008b;font-weight:bold">class</span> <span style="color:#008b45;font-weight:bold">Solution</span> {
    <span style="color:#228b22">/**
</span><span style="color:#228b22">     * @param A an integer array sorted in ascending order
</span><span style="color:#228b22">     * @param target an integer
</span><span style="color:#228b22">     * @return an integer
</span><span style="color:#228b22">     */</span>
    <span style="color:#8b008b;font-weight:bold">public</span> <span style="color:#00688b;font-weight:bold">int</span> <span style="color:#008b45">findPosition</span>(<span style="color:#00688b;font-weight:bold">int</span>[] nums, <span style="color:#00688b;font-weight:bold">int</span> target) {
        <span style="color:#8b008b;font-weight:bold">if</span> (nums == <span style="color:#8b008b;font-weight:bold">null</span> || nums.<span style="color:#658b00">length</span> == 0) {
            <span style="color:#8b008b;font-weight:bold">return</span> -1;
        }

        <span style="color:#00688b;font-weight:bold">int</span> start = 0, end = nums.<span style="color:#658b00">length</span> - 1;
        <span style="color:#8b008b;font-weight:bold">while</span> (start + 1 &lt; end) {
            <span style="color:#00688b;font-weight:bold">int</span> mid = start + (end - start) / 2;
            <span style="color:#8b008b;font-weight:bold">if</span> (nums[mid] == target) {
                <span style="color:#8b008b;font-weight:bold">return</span> mid;
            } <span style="color:#8b008b;font-weight:bold">else</span> <span style="color:#8b008b;font-weight:bold">if</span> (nums[mid] &lt; target) {
                start = mid;
            } <span style="color:#8b008b;font-weight:bold">else</span> {
                end = mid;
            }
        }

        <span style="color:#8b008b;font-weight:bold">if</span> (nums[start] == target) {
            <span style="color:#8b008b;font-weight:bold">return</span> start;
        }
        <span style="color:#8b008b;font-weight:bold">if</span> (nums[end] == target) {
            <span style="color:#8b008b;font-weight:bold">return</span> end;
        }
        <span style="color:#8b008b;font-weight:bold">return</span> -1;
    }
}

</code></pre></div><div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-go" data-lang="go">public class Solution {
    <span style="color:#228b22">/**
</span><span style="color:#228b22">     * @param A an integer array
</span><span style="color:#228b22">     * @return void
</span><span style="color:#228b22">     */</span>
    public void <span style="color:#008b45">sortIntegers2</span>(<span style="color:#00688b;font-weight:bold">int</span>[] A) {
        <span style="color:#228b22">// use a shared temp array, the extra memory is O(n) at least
</span><span style="color:#228b22"></span>        <span style="color:#00688b;font-weight:bold">int</span>[] temp = new <span style="color:#00688b;font-weight:bold">int</span>[A.length];
        <span style="color:#008b45">mergeSort</span>(A, <span style="color:#b452cd">0</span>, A.length - <span style="color:#b452cd">1</span>, temp);
    }

    private void <span style="color:#008b45">mergeSort</span>(<span style="color:#00688b;font-weight:bold">int</span>[] A, <span style="color:#00688b;font-weight:bold">int</span> start, <span style="color:#00688b;font-weight:bold">int</span> end, <span style="color:#00688b;font-weight:bold">int</span>[] temp) {
        <span style="color:#8b008b;font-weight:bold">if</span> (start &gt;= end) {
            <span style="color:#8b008b;font-weight:bold">return</span>;
        }

        <span style="color:#00688b;font-weight:bold">int</span> left = start, right = end;
        <span style="color:#00688b;font-weight:bold">int</span> mid = (start + end) / <span style="color:#b452cd">2</span>;

        <span style="color:#008b45">mergeSort</span>(A, start, mid, temp);
        <span style="color:#008b45">mergeSort</span>(A, mid + <span style="color:#b452cd">1</span>, end, temp);
        <span style="color:#008b45">merge</span>(A, start, mid, end, temp);
    }

    private void <span style="color:#008b45">merge</span>(<span style="color:#00688b;font-weight:bold">int</span>[] A, <span style="color:#00688b;font-weight:bold">int</span> start, <span style="color:#00688b;font-weight:bold">int</span> mid, <span style="color:#00688b;font-weight:bold">int</span> end, <span style="color:#00688b;font-weight:bold">int</span>[] temp) {
        <span style="color:#00688b;font-weight:bold">int</span> left = start;
        <span style="color:#00688b;font-weight:bold">int</span> right = mid + <span style="color:#b452cd">1</span>;
        <span style="color:#00688b;font-weight:bold">int</span> index = start;

        <span style="color:#228b22">// merge two sorted subarrays in A to temp array
</span><span style="color:#228b22"></span>        <span style="color:#008b45">while</span> (left &lt;= mid &amp;&amp; right &lt;= end) {
            <span style="color:#8b008b;font-weight:bold">if</span> (A[left] &lt; A[right]) {
                temp[index++] = A[left++];
            } <span style="color:#8b008b;font-weight:bold">else</span> {
                temp[index++] = A[right++];
            }
        }
        <span style="color:#008b45">while</span> (left &lt;= mid) {
            temp[index++] = A[left++];
        }
        <span style="color:#008b45">while</span> (right &lt;= end) {
            temp[index++] = A[right++];
        }

        <span style="color:#228b22">// copy temp back to A
</span><span style="color:#228b22"></span>        <span style="color:#8b008b;font-weight:bold">for</span> (index = start; index &lt;= end; index++) {
            A[index] = temp[index];
        }
    }
}
